sourcesContent:
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonInput, IonButton, IonCheckbox, IonIcon, IonSegment, IonSegmentButton, IonBadge, IonActionSheet, IonAlert, IonItemGroup, IonFooter, IonGrid } from '@ionic/react';
import React from 'react';
import './Home.css';
import { useToDoList } from '../hooks/useToDoList';
import { useState } from "react";
import { create, trash, close } from 'ionicons/icons';


const Home: React.FC = () => {
  // import des fonctions et variables
  const { addItem, listItems, toggleItem, removeAllItems, counterTodo, removeCounterTodo, addCounterTodo, removeItem, modifieItem } = useToDoList();

  // texte à ajouter pour un nouvel item
  const [text, setText] = useState<any>();

  // état du tri de la liste
  let [segmentValue, setSegmentValue] = useState('all');

  // état de l'affichage de la feuille d'action
  let [showActionSheet, setShowActionSheet] = useState(false);

  // état de l'affichage de l'alerte de modification de l'item
  let [showModifieAlert, setShowModifieAlert] = useState(false);

  // sélecteur de l'id de l'item à modifier ou à supprimer
  let [itemToModifie, setItemToModifie] = useState<number>();

  // état de l'affichage de l'alerte de suppression de toutes les tâches
  const [showDeleteAllAlert, setShowDeleteAllAlert] = useState(false);

  // fonction pour afficher les items dans la liste
  const displayItems = (value) => {

    // afficher un message différent si la liste est vide
    if (listItems.length === 0) {
      return <IonItem><IonLabel>Félicitation votre liste est vide</IonLabel></IonItem>
    }

    // sinon on affiche la liste 
    else {

      // pour afficher tous les items
      if (value === 'all') {

        return listItems.map((item) => {

          // ne pas afficher les items supprimés manuellement
          // trie des items non cochés afin de les compter
          if (item != undefined && item.checked === false) {
            return (
              <IonItem key={item.id}>
                <IonCheckbox slot="start" color="primary" checked={item.checked} onIonChange={(e) => {
                  toggleItem(item.id)
                  removeCounterTodo()
                }} />
                <IonLabel className={item.checked ? 'checked' : ''}>{item.text}</IonLabel>

                <IonButton fill='clear' onClick={() => {
                  setShowActionSheet(true)
                  setItemToModifie(item.id)
                }}><IonIcon icon={create} color="primary" slot="end" /></IonButton>


                <IonActionSheet
                  isOpen={showActionSheet}
                  onDidDismiss={() => setShowActionSheet(false)}
                  cssClass='my-custom-class'
                  buttons={[{
                    text: 'Supprimer',
                    role: 'destructive',
                    icon: trash,
                    handler: () => {
                      removeItem(itemToModifie)
                    }
                  },
                  {
                    text: 'Modifier',
                    icon: create,
                    handler: () => {
                      setShowModifieAlert(true)
                    }
                  }, {
                    text: 'Annuler',
                    icon: close,
                    role: 'cancel',
                  }]}
                >
                </IonActionSheet>



                <IonAlert
                  isOpen={showModifieAlert}
                  onDidDismiss={() => setShowModifieAlert(false)}
                  header={'Renommez votre tâche'}
                  inputs={[
                    {
                      name: 'text',
                      type: 'text',
                      placeholder: 'Nouveau nom de la tâche',
                    }]}
                  buttons={[
                    {
                      text: 'Annuler',
                      role: 'cancel',
                      cssClass: 'secondary',
                    },
                    {
                      text: 'Valider',
                      handler: data => {
                        modifieItem(itemToModifie, data.text)
                      }
                    }
                  ]}
                />
              </IonItem>
            )
          }

          // on affiche les items cochés sans les compter
          else if (item != undefined) {
            return (
              <IonItem key={item.id}>
                <IonCheckbox slot="start" color="primary" checked={item.checked} onIonChange={(e) => {
                  toggleItem(item.id)
                  addCounterTodo()
                }} />
                <IonLabel className={item.checked ? 'checked' : ''}>{item.text}</IonLabel>

                <IonButton fill='clear' onClick={() => {
                  setShowActionSheet(true)
                  setItemToModifie(item.id)
                }}><IonIcon icon={create} color="primary" slot="end" /></IonButton>


                <IonActionSheet
                  isOpen={showActionSheet}
                  onDidDismiss={() => setShowActionSheet(false)}
                  cssClass='my-custom-class'
                  buttons={[{
                    text: 'Supprimer',
                    role: 'destructive',
                    icon: trash,
                    handler: () => {
                      removeItem(itemToModifie)
                    }
                  },
                  {
                    text: 'Modifier',
                    icon: create,
                    handler: () => {
                      setShowModifieAlert(true)
                    }
                  }, {
                    text: 'Annuler',
                    icon: close,
                    role: 'cancel',
                  }]}
                >
                </IonActionSheet>

                <IonAlert
                  isOpen={showModifieAlert}
                  onDidDismiss={() => setShowModifieAlert(false)}
                  header={'Renommez votre tâche'}
                  inputs={[
                    {
                      name: 'text',
                      type: 'text',
                      placeholder: 'Nouveau nom de la tâche',
                    }]}
                  buttons={[
                    {
                      text: 'Annuler',
                      role: 'cancel',
                      cssClass: 'secondary',
                    },
                    {
                      text: 'Valider',
                      handler: data => {
                        modifieItem(itemToModifie, data.text)
                      }
                    }]} /></IonItem>)
          }
        })
      }

      // trie des items non cochés
      else if (value === 'unchecked') {
        return (
          listItems.map((item) => {
            // ne pas afficher les items supprimés manuellement
            // traite uniquement les items non cochés
            if (item != undefined && item.checked === false) {
              return (
                < IonItem key={item.id} >
                  <IonCheckbox slot="start" color="primary" checked={item.checked} onIonChange={(e) => {
                    toggleItem(item.id)
                    removeCounterTodo()
                  }} />
                  <IonLabel className={item.checked ? 'checked' : ''}>{item.text}</IonLabel>

                  <IonButton fill='clear' onClick={() => {
                    setShowActionSheet(true)
                    setItemToModifie(item.id)
                  }}><IonIcon icon={create} color="primary" slot="end" /></IonButton>


                  <IonActionSheet
                    isOpen={showActionSheet}
                    onDidDismiss={() => setShowActionSheet(false)}
                    cssClass='my-custom-class'
                    buttons={[{
                      text: 'Supprimer',
                      role: 'destructive',
                      icon: trash,
                      handler: () => {
                        removeItem(itemToModifie)
                      }
                    },
                    {
                      text: 'Modifier',
                      icon: create,
                      handler: () => {
                        setShowModifieAlert(true)
                      }
                    }, {
                      text: 'Annuler',
                      icon: close,
                      role: 'cancel',
                    }]}
                  >
                  </IonActionSheet>

                  <IonAlert
                    isOpen={showModifieAlert}
                    onDidDismiss={() => setShowModifieAlert(false)}
                    header={'Renommez votre tâche'}
                    inputs={[
                      {
                        name: 'text',
                        type: 'text',
                        placeholder: 'Nouveau nom de la tâche',
                      }]}
                    buttons={[
                      {
                        text: 'Annuler',
                        role: 'cancel',
                        cssClass: 'secondary',
                      },
                      {
                        text: 'Valider',
                        handler: data => {
                          modifieItem(itemToModifie, data.text)
                        }
                      }
                    ]}
                  /></IonItem >)
            }
          }))
      }
    }
  }


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>To do list</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">To do list</IonTitle>
          </IonToolbar>
        </IonHeader>

        <IonSegment className='padding-lr' onIonChange={e => setSegmentValue(e.detail.value)}>
          <IonSegmentButton value="all">
            <IonLabel>Tout</IonLabel>
          </IonSegmentButton>
          <IonSegmentButton value="unchecked">
            <IonLabel>À faire <IonBadge color="primary">{counterTodo}</IonBadge></IonLabel>

          </IonSegmentButton>
        </IonSegment>


        <IonList className='margin-bottom-120'>
          {
            displayItems(segmentValue)
          }
        </IonList>


        <IonFooter className='footer'>

          <IonItemGroup>

            <IonItem>
              <IonButton color="primary" type="submit" onClick={() => { setShowDeleteAllAlert(true) }}>Vider totalement la liste</IonButton>
            </IonItem>

            <IonAlert
              isOpen={showDeleteAllAlert}
              onDidDismiss={() => setShowDeleteAllAlert(false)}
              cssClass='my-custom-class'
              header={'Voulez vous vraiment'}
              message={'Message <strong>text</strong>!!!'}
              buttons={[
                {
                  text: 'Annuler',
                  role: 'cancel',
                  cssClass: 'secondary',
                  handler: blah => {
                    console.log('Confirm Cancel: blah');
                  }
                },
                {
                  text: 'Supprimer toutes les tâches',
                  handler: () => {
                    removeAllItems()
                  }
                }
              ]}
            />

            <IonItem>
              <IonInput placeholder="Qu'avez-vous en tête ?" inputmode="text" clearInput autofocus required onIonChange={e => { setText(e.detail.value); }}></IonInput>
              <IonButton color="primary" type="submit" onClick={() => { addItem(text) }}>Créer</IonButton>
            </IonItem>

          </IonItemGroup>

        </IonFooter>

      </IonContent>


    </IonPage>
  );
};

export default Home;
